<script>
	import { useSession } from '@app/session/context.js';

	const session = useSession();
	console.log('worldname session', session);

	export let name;
	export let isMention = false;
	export let classes = '';
	export let innerClasses = '';

	$: isSession = name === $session.person.slug;
	$: colorCls = isSession ? 'text-purple-700' : 'text-green-700';
</script>

<a href={name} class="hover:underline {classes}">
	<small class="{colorCls} {innerClasses}">
		{#if isMention}<span class="opacity-50">@</span>{/if}{name}
	</small>
</a>
