<script>
	export let name;
	export let classes = '';

	const SESSION_PERSON_NAME = 'ryan';

	$: isSession = name === SESSION_PERSON_NAME;
	$: colorCls = isSession ? 'text-purple-700' : 'text-green-700';
</script>

<a href={name} class="hover:underline {classes}">
	<small class={colorCls}>
		<span class="opacity-50">@</span>{name}
	</small>
</a>
